import{c as e}from"./Admonition.DdfWBigK.js";import{F as i,$ as s}from"./keystatic-page.CX2dnrWP.js";import"./___vite-browser-external_commonjs-proxy.B2SwpVhP.js";import"./_commonjsHelpers.C4iS2aBk.js";import"./index.zSnisxZ3.js";/* empty css                               */import"./astro/assets-service.CJMMCYvq.js";const l={src:"/astro-assets/Webflow gui.do.C_oqsq3E.jpeg",width:1407,height:649,format:"jpg"},c={src:"/astro-assets/Webflow gui.do (1).BGKiQSn0.jpeg",width:810,height:1206,format:"jpg"},h={title:" Often Confused Commerce Terms ",description:"Lets set the record straight about some often mixed up terms in commerce",draft:!1,authors:["gxjansen"],pubDate:"2022-03-15T00:00:00.000Z",heroImage:"../often-confused-commerce-terms/heroImage.png",categories:["Open Source","Spryker"]};function b(){return[{depth:2,slug:"terminology",text:"Terminology"},{depth:2,slug:"debunking-the-statements",text:"Debunking the statements"},{depth:3,slug:"open-source-has-to-be-deployedrunmanaged-on-your-own-need-people-on-call-247",text:"”Open source has to be deployed/run/managed on your own. Need people on call 24/7”"},{depth:3,slug:"open-source-by-definition-is-versioned",text:"”Open source by definition is versioned.”"},{depth:3,slug:"as-new-updates-are-available-you-have-to-deploymanage-them-yourself-urgent-security-patch-you-need-to-apply-and-deploy-immediately-yourself",text:"”As new updates are available, you have to deploy/manage them yourself. Urgent security patch? You need to apply and deploy immediately. Yourself”"},{depth:3,slug:"open-source-is-most-likely-to-be-monolithic-in-nature",text:"”Open source is most likely to be monolithic in nature…”"},{depth:3,slug:"because-you-wouldnt-want-a-large-community-to-have-to-run-dozens-of-individual-microservices",text:"”…because you wouldn’t want a large community to have to run dozens of individual microservices”"},{depth:3,slug:"open-source-requires-that-you-layer-your-updates-over-the-top-of-the-code-thereby-tightly-coupling-your-code-to-the-open-source-code",text:"”Open source requires that you layer your updates over the top of the code, thereby tightly coupling your code to the open source code”"},{depth:3,slug:"theres-no-incentive-for-an-open-source-community-to-build-a-big-multi-tenant-saas-application-for-a-vendor-to-run",text:"”There’s no incentive for an open source community to build a big multi tenant SaaS application for a vendor to run.”"},{depth:2,slug:"recap",text:"Recap"},{depth:2,slug:"next-time",text:"Next time…"}]}const v=!0;function a(t){const o={a:"a","astro-image":"astro-image",blockquote:"blockquote",em:"em",h2:"h2",h3:"h3",li:"li",p:"p",strong:"strong",ul:"ul",...t.components},r=o["astro-image"];return e(i,{children:[e(o.p,{children:"Recently I’ve seen some (often absolute) statements going around, generally in the line of “open source commerce platforms are a terrible idea”. Now of course different solutions always have different pros and cons."}),`
`,e(o.p,{children:"But I see quite a bit of attributes being assigned to open source software that are either not at all representative of all open source solutions OR not at all exclusive to open source."}),`
`,e(o.p,{children:["Terms like ",e(o.strong,{children:"open source"}),", ",e(o.strong,{children:"monolithic, single-tenant"})," and ",e(o.strong,{children:"on-premise"})," are put in a box on one side of the equation and ",e(o.strong,{children:"microservices, multi-tenant"})," and ",e(o.strong,{children:"hosted/SaaS"})," on the other hand, often implying that everything in the box is the same."]}),`
`,e(o.p,{children:"Sure, these terms are related and some might often co-exist. But they are far from the same thing and understanding the difference is crucial when you’re doing research for your next commerce platform."}),`
`,e(o.blockquote,{children:[`
`,e(o.p,{children:["Just like ",e(o.strong,{children:"green"}),", ",e(o.strong,{children:"apple"})," and ",e(o.strong,{children:"fruit"})," are related, it’s silly to state that all fruits are apples, all apples are green or all green things are fruits. That just doesn’t make sense."]}),`
`]}),`
`,e(o.h2,{id:"terminology",children:"Terminology"}),`
`,e(o.p,{children:"Before going through some specific statements I’ve seen around, let’s start with a boring terminology list (with additional commerce context) so we’re all on the same page. Please note that I’m by no means the gatekeeper of commerce terms, but I thought it would be useful for others that are also confused by these terms to provide some clarity on things."}),`
`,e(o.ul,{children:[`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Open source:"})," this could refer to two things: (1) is the source code “public/visible” to everyone who wants to see it and/or (2) the type of license model of the software determining the legal use of the software (see ",e(o.a,{href:"https://opensource.org/licenses",children:"opensource.org"})," for an overview). For example: Shopware has both (1) and (2), Spryker has (1) and Adobe Commerce Cloud has neither."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"On-premise:"})," software that is hosted at the location or at least on servers owned/maintained by the merchant itself (or by a 3rd party hired by the merchant).  "]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Monolithic:"})," a single piece of software that combines a lot of related features that are often intertwined and not easily separated. Adobe Commerce is often mentioned as an example here (see ",e(o.a,{href:"https://en.wikipedia.org/wiki/Monolithic_application",children:"Monolithic Application"}),")."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Microservice:"})," Microservices - also known as the microservice architecture - is an architectural style that structures an application as a collection of services that are (see ",e(o.a,{href:"https://en.wikipedia.org/wiki/Microservices",children:"Microservices"}),"). To run a full-fledged commerce store, you’d need tens if not hundreds of microservices that tie together."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Packaged Business Capabilities:"})," PBCs represent a middleground between microservices and monolithic platforms. Their ability to work independently of other PBCs allows agile updates, changes and extensions, with no interference to the tech stack as a whole. Some example PBCs: PIM, Offer management, Tax management, CMS, Storefront, Gift cards… etc etc"]}),`
`]}),`
`]}),`
`,e(o.p,{children:["   ",e(r,{alt:"",src:l})]}),`
`,e(o.ul,{children:[`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Single-tenant"}),": A single instance of the software and supporting infrastructure serve a single customer. Essentially, there is no sharing happening with this option."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"Multi-tenant"}),": A single instance of the software and its supporting infrastructure serves multiple customers. Each customer shares the software application and often also shares a single database. Each tenant’s data is isolated and remains invisible to other tenants."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"SaaS"}),": Software as a Service: Applications which are managed and delivered by a third-party vendor, to its users. A majority of SaaS applications run directly through your web browser. Example: Shopify."]}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:[e(o.strong,{children:"PaaS"}),": Platform as a Service: a complete development and deployment environment in the cloud, with resources that enable developers to deliver everything from simple cloud-based apps to sophisticated, cloud-enabled enterprise applications. Example: Spryker."]}),`
`]}),`
`]}),`
`,e(o.p,{children:["I don’t want to write a whole PaaS vs SaaS explainer here, but ",e(o.a,{href:"https://spryker.com/en/blog/paas-saas-or-both?utm_source=gui.do&utm_medium=website&utm_campaign=Guido_X_Jansen",children:"PaaS, Saas or Both"})," is nice blog post explaining the differences if you want to dive deeper into that. Spryker also created a nice ",e(o.a,{href:"https://spryker.com/en/decision-tree-cloud?utm_source=gui.do&utm_medium=website&utm_campaign=Guido_X_Jansen",children:"PaaS or SaaS or on-premise decision tree"})," for you to get a feel for the differences:"]}),`
`,e(o.p,{children:[e(r,{alt:"",src:c}),"   "]}),`
`,e(o.h2,{id:"debunking-the-statements",children:"Debunking the statements"}),`
`,e(o.p,{children:"So now that we have some basics covered, lets circle back to some of the specific statements I’ve encountered and how I personally see these. I’ve copied these 1-on-1 from some posts."}),`
`,e(o.h3,{id:"open-source-has-to-be-deployedrunmanaged-on-your-own-need-people-on-call-247",children:e(o.em,{children:e(o.strong,{children:"”Open source has to be deployed/run/managed on your own. Need people on call 24/7”"})})}),`
`,e(o.p,{children:[e(o.strong,{children:"All"})," software has to be deployed/run/managed, open source or otherwise. As a merchant you can pull these tasks/responsibilities towards you or move it away from you:"]}),`
`,e(o.ul,{children:[`
`,e(o.li,{children:[`
`,e(o.p,{children:"You can have your own people to do it."}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:"You can hire a 3rd party agency / system integrator to do it."}),`
`]}),`
`,e(o.li,{children:[`
`,e(o.p,{children:"You can let the company that made the software do it and maybe they even host it for you."}),`
`]}),`
`]}),`
`,e(o.p,{children:["There are several open source commerce platforms that offer their software in a SaaS/PaaS variant, sometimes in addition to an on-premise variant: ",e(o.a,{href:"https://spryker.com?utm_source=gui.do&utm_medium=website&utm_campaign=Guido_X_Jansen",children:"Spryker"}),", ",e(o.a,{href:"https://woocommerce.com/",children:"Woocommerce"}),", ",e(o.a,{href:"https://www.shopware.com/",children:"Shopware"})," and also PIM solutions like ",e(o.a,{href:"https://www.akeneo.com/",children:"Akeneo"})," all do this."]}),`
`,e(o.p,{children:["So it’s ",e(o.strong,{children:"perfectly possible"})," to run open source as a SaaS service and not having to worry about deploying/running/managing it as a merchant."]}),`
`,e(o.p,{children:"The “on your own”’ part is also not valid. Lot’s of agencies thrive by implementing the open source software platforms mentioned above and maintaining it for merchants (whether that’s hosted on-premise or through specialized hosting services or even on SaaS platforms)."}),`
`,e(o.p,{children:"Of course it is very true that when you compare software that you host on your own servers versus a SaaS or a PaaS solution that the latter will “release” you as the merchants from a lot of work in deploying/running/managing the software. You basically outsource that responsibility to the vendor and that’s why you pay them a monthly fee."}),`
`,e(o.h3,{id:"open-source-by-definition-is-versioned",children:e(o.em,{children:e(o.strong,{children:"”Open source by definition is versioned.”"})})}),`
`,e(o.p,{children:"No, that’s not in the definition of “Open Source”. But yes: unless the piece of software was released once and never been updated or maintained it probably is versioned. As is all software: this has nothing to do with the software being open source."}),`
`,e(o.p,{children:"From a customer point of view, most SaaS offerings are indeed “versionless”: you still get new versions and there is no work from your side and new features of bugfixes just seem to appear. And most of the time, all customers will be on the same version, which means much less maintenance for the vendor."}),`
`,e(o.p,{children:"This is not a flawless system as APIs might be updated/created/deprecated along the way, but obviously requires much less from the merchant compared to any self-hosted software (including but not limited to open source)."}),`
`,e(o.h3,{id:"as-new-updates-are-available-you-have-to-deploymanage-them-yourself-urgent-security-patch-you-need-to-apply-and-deploy-immediately-yourself",children:e(o.em,{children:e(o.strong,{children:"”As new updates are available, you have to deploy/manage them yourself. Urgent security patch? You need to apply and deploy immediately. Yourself”"})})}),`
`,e(o.p,{children:"That’s your choice: you can probably use services provided by either the software vendor itself, use an agency in their ecosystem or indeed deploy/manage these yourself. Not every piece of open software will have a software vendor or agencies to do this for you, but the popular open source commerce platforms do."}),`
`,e(o.p,{children:"Many (open source and other) platforms have semi-automated or even fully automated update features, like Spryker and Woocommerce do. And yes: clicking a couple buttons is more work then the automagic updates that SaaS products provide, but ass long as you follow the coding standards, updating your system has just a minor impact on your overall workload in maintaining those systems."}),`
`,e(o.p,{children:"And in case you add (a lot of) custom code or external services to your SaaS service, like for example a VueJS or React frontend, you will still have this update “problem”, maybe not for your core commerce product, but you do for any customizations."}),`
`,e(o.p,{children:"So whichever way you go: at the end of the day someone needs to deal with updates to parts of the system. At least when the code is open, things are visible and as a merchant you can have control if you want to."}),`
`,e(o.p,{children:"When using PaaS/SaaS you replace that control with trusting and external party to do so which some merchants don’t like to do. Your SaaS provider might just as well have many unpatched systems running, you’re just not made aware of it (until things go horribly wrong). Is that inherently better…?"}),`
`,e(o.h3,{id:"open-source-is-most-likely-to-be-monolithic-in-nature",children:e(o.em,{children:e(o.strong,{children:"”Open source is most likely to be monolithic in nature…”"})})}),`
`,e(o.p,{children:["I don’t know about the statistics of this. But since open source has been around for a long time and non-monolithic commerce platforms are relatively new this is probably true. But again this applies when you consider ",e(o.strong,{children:"all"})," software and ",e(o.strong,{children:"all"})," commerce platforms and this is ",e(o.strong,{children:"not"})," an inherent feature of open source."]}),`
`,e(o.h3,{id:"because-you-wouldnt-want-a-large-community-to-have-to-run-dozens-of-individual-microservices",children:e(o.em,{children:e(o.strong,{children:"”…because you wouldn’t want a large community to have to run dozens of individual microservices”"})})}),`
`,e(o.p,{children:"If a good functioning community can maintain a big monolithic system, I don’t see why they couldn’t maintain a system based on microservices. Since everything is decoupled I would actually argue it’s easier, because all functions are isolated and you don’t have to always keep thinking about the impact you might have on the system as a whole."}),`
`,e(o.p,{children:["And that’s again not a hypothetical: lot of people ",e(o.strong,{children:"do"})," maintain microservices. But exactly ",e(o.strong,{children:"because"})," these are ",e(o.em,{children:e(o.strong,{children:"micro"})}),"services, you don’t register these as “a large community that runs dozens of individual microservices”."]}),`
`,e(o.p,{children:["But that doesn’t mean it doesn’t happen: there’s ",e(o.a,{href:"https://www.docker.com/",children:"Docker"}),", ",e(o.a,{href:"https://www.terraform.io/",children:"Terraform"}),", ",e(o.a,{href:"https://helm.sh/",children:"K8 helm charts"})," and more tools that facilitate this."]}),`
`,e(o.p,{children:"So this actually already happens. A lot."}),`
`,e(o.h3,{id:"open-source-requires-that-you-layer-your-updates-over-the-top-of-the-code-thereby-tightly-coupling-your-code-to-the-open-source-code",children:e(o.em,{children:e(o.strong,{children:"”Open source requires that you layer your updates over the top of the code, thereby tightly coupling your code to the open source code”"})})}),`
`,e(o.p,{children:"This seems to be more of a monolithic versus decoupled/microservices argument. There’s some truth in this statement in that case, but again has nothing to do with the code being open source."}),`
`,e(o.h3,{id:"theres-no-incentive-for-an-open-source-community-to-build-a-big-multi-tenant-saas-application-for-a-vendor-to-run",children:e(o.em,{children:e(o.strong,{children:"”There’s no incentive for an open source community to build a big multi tenant SaaS application for a vendor to run.”"})})}),`
`,e(o.p,{children:"We have Wordpress/WooCommerce, Shopware, Akeneo and Spryker to prove this wrong. Open source projects don’t have to be run by a community of volunteer developers that have no incentive to do so: These open source projects are all backed or simply run by companies that apparently have enough incentive to do so."}),`
`,e(o.h2,{id:"recap",children:"Recap"}),`
`,e(o.p,{children:"So I hope this gives some clarity. If you need more clarification on some of the explanations above or have your own thoughts about this? Put them in the comments!"}),`
`,e(o.h2,{id:"next-time",children:"Next time…"}),`
`,e(o.p,{children:"…I’ll post an article on the “Commerce is a Commodity” versus “Commerce is Custom” movements."}),`
`,e(o.p,{children:e(o.em,{children:["PS: if you want to read more about this, Sander Mangel also published an article about this topic today titled ",e(o.a,{href:"https://www.linkedin.com/pulse/why-saas-vs-open-source-doesnt-have-clear-cut-answer-sander-mangel/?published=t",children:"“Why SaaS vs. Open Source doesn’t have a clear cut answer”"})," and I highly recommend you give that a read."]})}),`
`,e(o.p,{children:"‍"})]})}function d(t={}){const{wrapper:o}=t.components||{};return o?e(o,{...t,children:e(a,{...t})}):a(t)}const S="src/content/blog/en/often-confused-commerce-terms/index.mdx",_="/Users/gxjansen/Documents/GitHub/gxjansen.github.io/src/content/blog/en/often-confused-commerce-terms/index.mdx",n=(t={})=>d({...t,components:{Fragment:i,...t.components,"astro-image":t.components?.img??s}});n[Symbol.for("mdx-component")]=!0;n[Symbol.for("astro.needsHeadRendering")]=!h.layout;n.moduleId="/Users/gxjansen/Documents/GitHub/gxjansen.github.io/src/content/blog/en/often-confused-commerce-terms/index.mdx";export{n as Content,v as __usesAstroImage,n as default,_ as file,h as frontmatter,b as getHeadings,S as url};
